import React from 'react';
import './SearchBar.css';

let sortByOptions = {'Best match': 'best_match',
'Highest Rated': 'rating',
'Most Reviewed': 'review_count'};

export class SearchBar extends React.Component {

   constructor(props) {
	   super(props);
	   this.handleTermChange = this.handleTermChange.bind(this);
	   this.handleLocationChange = this.handleLocationChange.bind(this);
	   this.state = {
		   term: '',
		   location: '',
		   sortBy: 'best_match'
		   
	   }
   }

   renderSortByOptions() {
      return Object.keys(sortByOptions).map(sortByOption => {
         let sortByOptionValue = sortByOptions[sortByOption];
         return <li key={sortByOptionValue} className={this.getSortByClass(sortByOptionValue)} onClick={this.handleSortByChange.bind(this, sortByOptionValue)} >{sortByOption}</li>})
}

	getSortByClass(sortByOption) {
		if (this.state === sortByOption) {
			return 'active';
		}
		else { return ''}
	}
	
	handleSortByChange(sortByOption) {
		this.state.sortBy = this.setState({});
	}

	handleTermChange(event) {
		this.state = this.setState({
			term: event.target.value
		});
	}
	
	handleLocationChange(event) {
		this.state = this.setState({
			location: event.target.value
		});
	}
	
   render() {
      return (
<div className="SearchBar">
  <div className="SearchBar-sort-options">
    <ul>
	{this.renderSortByOptions()}
    </ul>
  </div>
  <div className="SearchBar-fields">
    <input placeholder="Search Businesses" onChange={this.handleTermChange} onChange={this.handleLocationChange}/>
    <input placeholder="Where?" onChange={this.handleTermChange} onChange={this.handleLocationChange}/>
  </div>
  <div className="SearchBar-submit">
    <a href="/">Let's Go</a>
  </div>
</div>)
};
}; 

export default SearchBar;
